sp_search_source.data_fetch_script:
(function(query) {
	var results = [];

	//TODO:Need to do a contains as well
	var apis = new GlideRecord("x_15401_data_gover_api");
	//apis.addQuery('123TEXTQUERY321', query);
	apis.addQuery('active', true);
	apis.setLimit(data.limit);
	apis.query();
	while (apis.next()) {
		var api = {}; 
		api.name = apis.name;
		api.description = apis.description;
		results.push(api);
		//gs.log("Hello");
	}

	$sp.logSearch('data_governance_api', query, apis.getRowCount(), data.searchType);	

	return results;
})(query);

