sys_variable_value.value:
var communities = [];
var arrayUtil = new global.ArrayUtil();
var insertRecord = new x_15401_data_gover.InsertRecordsIntoManyTable();
var addDF = new GlideRecord('x_15401_data_gover_m2m_additional_df_to_dsr');
addDF.addEncodedQuery('data_field.represents_business_term.communityISNOTEMPTY^data_sharing_request=' + current.sys_id);
addDF.query();
while(addDF.next()) {
	var addCommunity = addDF.data_field.represents_business_term.community.sys_id.toString();
	if(!arrayUtil.contains(communities, addCommunity)){
		communities.push(addCommunity);
	}
}
var reqDF = new GlideRecord('x_15401_data_gover_m2m_required_df_to_dsr');
reqDF.addEncodedQuery('data_field.represents_business_term.communityISNOTEMPTY^data_sharing_request=' + current.sys_id);
reqDF.query();
while(reqDF.next()) {
	var reqCommunity = reqDF.data_field.represents_business_term.community.sys_id.toString();
	if(!arrayUtil.contains(communities, reqCommunity)) {
		communities.push(reqCommunity);
	}
}
communities = arrayUtil.unique(communities);
for(var c = 0; c < communities.length; c++){
    var dsa = new GlideRecord('x_15401_data_gover_data_sharing_agreement');
	dsa.initialize();
	var communityId = communities[c];
	var com = new GlideRecord('x_15401_data_gover_community');
	var communityName = "";
	if(com.get(communityId)) {
		communityName = com.name;
	} else {
		communityName = "ERROR: " + c;
	}
	dsa.data_sharing_request = current.sys_id;
	dsa.community = communityId;
	dsa.application_name = current.application_name;
	dsa.description_of_need = current.description_of_need;
	dsa.description_of_project = current.description_of_project;
	dsa.scope_of_project = current.scope_of_project;
	dsa.name = current.name + " - " + communityName;
	dsa.sponsor = current.sponsor;
	dsa.requester = current.requester;
	dsa.watch_list = current.watch_list; 
	dsa.development_group = current.development_group;
	dsa.application_description = current.application_description;
	dsa.insert();
	//Find API Records
	insertRecord.insertEncodedRecordsM2M('x_15401_data_gover_m2m_dsr_to_api', 'data_sharing_request=' + current.sys_id, 'x_15401_data_gover_api', 'x_15401_data_gover_m2m_data_sharin_apis', 'data_sharing_agreement', 'api', dsa.sys_id, 'api');
	insertRecord.insertEncodedRecordsM2M('x_15401_data_gover_m2m_additional_df_to_dsr', 'data_field.represents_business_term.community=' + communityId + '^data_sharing_request=' + current.sys_id, 'x_15401_data_gover_data_field', 'x_15401_data_gover_m2m_additional_df_to_dsa', 'data_sharing_agreement', 'data_field', dsa.sys_id, 'data_field');
	insertRecord.insertEncodedRecordsM2M('x_15401_data_gover_m2m_required_df_to_dsr', 'data_field.represents_business_term.community=' + communityId + '^data_sharing_request=' + current.sys_id, 'x_15401_data_gover_data_field', 'x_15401_data_gover_m2m_required_df_to_dsa', 'data_sharing_agreement', 'data_field', dsa.sys_id, 'data_field');
	//Putting at the end of loop to fire off the DSA Workflow
	dsa.status = "Custodian";
	dsa.update();
}

